#!/bin/bash

if [ $# -ne 1 ];then
    printf "\n$0 <nmap output ports>\n\n";exit 1; fi

clear
printf "" | base64 -d 
ports=$(echo "$1" | grep open | awk '{print $1}' | cut -d'/' -f1 | tr '\n' ',' | sed s/.$//g)
printf "\nsudo nmap -sSV -Pn -T4 -vvv -p$ports <host>"
printf "\nsudo nmap -sSVC -Pn -T4 -vvv -p$ports <host>"
